	class CiscoFixupTests : DataModelCollector
	{
		[Test]
		public void OddLengthHighBitSetTest()
		{
			// standard test

			string xml = "<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n" +
				"<Peach>" +
				"   <DataModel name=\"TheDataModel\">" +
				"       <Number name=\"Cdp\" size=\"16\" signed=\"false\" endian=\"big\">" +
				"           <Fixup class=\"CiscoFixup\">" +
				"               <Param name=\"ref\" value=\"Data1\"/>" +
				"           </Fixup>" +
				"       </Number>" +
				"       <Blob name=\"Data1\" valueType=\"hex\" value=\"\"/>" +
				"   </DataModel>" +

				"   <StateModel name=\"TheState\" initialState=\"Initial\">" +
				"       <State name=\"Initial\">" +
				"           <Action type=\"output\">" +
				"               <DataModel ref=\"TheDataModel\"/>" +
				"           </Action>" +
				"       </State>" +
				"   </StateModel>" +

				"   <Test name=\"Default\">" +
				"       <StateModel ref=\"TheState\"/>" +
				"       <Publisher class=\"Null\"/>" +
				"   </Test>" +
				"</Peach>";

			PitParser parser = new PitParser();

			Peach.Core.Dom.Dom dom = parser.asParser(null, new MemoryStream(ASCIIEncoding.ASCII.GetBytes(xml)));

			RunConfiguration config = new RunConfiguration();
			config.singleIteration = true;

			Engine e = new Engine(this);
			e.startFuzzing(dom, config);

			// verify values
			byte[] precalcChecksum = new byte[] { 0x09, 0xa0 };
			Assert.AreEqual(1, values.Count);
			byte[] val = values[0].ToArray();
			Assert.AreEqual(precalcChecksum, val);
		}

